import api from"./api";export const getUsuarios=async()=>{try{const{data:data}=await api.get("/usuarios");return data}catch(error){throw new Error(error.response.data.error||"Error al cargar usuarios")}};export const crearUsuario=async datosUsuario=>{try{const{data:data}=await api.post("/usuarios",datosUsuario);return data}catch(error){throw new Error(error.response.data.error||"Error al crear usuario")}};export const actualizarUsuario=async({id:id,...datosUsuario})=>{try{const{data:data}=await api.put(`/usuarios/${id}`,datosUsuario);return data}catch(error){throw new Error(error.response.data.error||"Error al actualizar usuario")}};export const eliminarUsuario=async id=>{try{await api.delete(`/usuarios/${id}`)}catch(error){throw new Error(error.response.data.error||"Error al eliminar usuario")}};